import React, { useState } from "react";
import "./index.css";


interface SplitBubbleProps {
  disabled: boolean
}

const SplitBubble = (props: SplitBubbleProps) => {
  const [isMyBubbleSpplitted, setIsMyBubbleSplitted] = useState(false);

  return (
    <div className="flex justify-center items-center w-full h-full">
      {isMyBubbleSpplitted ? (
        <div className="flex flex-col w-full h-full rounded-full">
          <div className="flex flex-row justify-between w-full h-full">
            <SplitBubble disabled />
            <SplitBubble disabled />
          </div>
          <div className="flex flex-row justify-between w-full h-full">
            <SplitBubble disabled />
            <SplitBubble disabled />
          </div>
        </div>
      ) : (
        <div
          onClick={() => setIsMyBubbleSplitted(props.disabled ? false : true)}
          className={`flex w-1/2 h-1/2 rounded-full bg-red-600`}
        />
      )}
    </div>
  );
};

function App() {
  return (
    <div className="w-screen min-h-screen h-screen flex flex-row bg-[#E5E5E5]">
      <div className="flex justify-center items-center h-full w-full p-32">
        <div className="flex justify-center items-center w-2/3 aspect-square bg-white drop-shadow-md rounded-xl">
          <SplitBubble disabled={false} />
        </div>
      </div>
    </div>
  );
}

export default App;

/*
  - tailwind
  - no pixel css, only pourcentage
  - responsive page
  - form to change color (panel width fix --> width: 24rem or 384px )
  - minimum UI realisation (grey: #E5E5E5, darkGrey: #60657A)
  - when click on bubble it should split on 4 smaller bubble (the new smaller bubble should be 1/2 of the previous bubble)
*/
