import { useQuery } from 'react-query';
import { useEffect, useState } from 'react';
import { Button, Divider } from '@mui/material';
import TableBTC from './TableBTC';
import TableDessert from './TableDessert';

export type DessertDetail = {
  name: string,
  calories: number,
  fat: number,
  carbs: number,
  protein: number
}

export type PriceType = {
  code: string,
  symbol: string,
  rate: string,
  description: string,
  rate_float: string
}

const getPriceBTC = async () => {
  const res = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
  return res.json();
};

const PRICE_BTC = "priceBTC"

const Dashboard = () => {
  const query = useQuery(PRICE_BTC, getPriceBTC)

  const [btcPrice, setBtcPrice] = useState<PriceType[] | null>(query.data ? [query.data.bpi.USD, query.data.bpi.GBP, query.data.bpi.EUR] : null)

  useEffect(() => {
    if (btcPrice === null) {
      setBtcPrice(query.data)
    }
  }, [btcPrice, query.data, setBtcPrice])

  return (
    <>
      <h2 className="text-xl text-gray-500 font-bold pt-4">
        Dessert 
      </h2>
      <TableDessert />  
      <div className='py-4'>
        <Divider />
      </div>
      <div className='flex flex-row justify-between items-center'>
        <h2 className="text-xl text-gray-500 font-bold">
          BTC Price 
        </h2>
        <Button
          variant='contained'
          onClick={async () => {
          await query.refetch()
          setBtcPrice([query.data.bpi.USD, query.data.bpi.GBP, query.data.bpi.EUR])
        }}>
          Refresh
        </Button>
      </div>
      <TableBTC btcPrice={btcPrice} setBtcPrice={setBtcPrice} />
    </>
  );
}

export default Dashboard
