import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import Divider from '@mui/material/Divider';
import { useForm } from 'react-hook-form';
import { useTypedController } from "@hookform/strictly-typed"
import { Button, TextField } from '@mui/material';

type DessertDetail = {
  name: string,
  calories: number,
  fat: number,
  carbs: number,
  protein: number
}

type DrawerListProps = {
  data: DessertDetail | null,
  onClose: () => void
  saveForm: React.Dispatch<React.SetStateAction<DessertDetail[]>>
}

const DrawerMainTable = (props: DrawerListProps) => {
  const { data, onClose, saveForm } = props

  // const { register, control, handleSubmit } = useForm<DessertDetail>(data)
  const { handleSubmit, control } = useForm<DessertDetail>({
    mode: "onChange",
    defaultValues: data ?? { name: "", calories: 0, fat: 0, carbs: 0, protein: 0 }
  })
  const TypedController = useTypedController<DessertDetail>({ control })

  const handleOnSubmit = (form: DessertDetail) => {
    // onSubmit((form))
  }

  const DrawerList = (
    <form onSubmit={handleSubmit(handleOnSubmit)}>
      <Box sx={{ width: 600 }} className="p-8" role="presentation" onClick={onClose}>
        <h1 className="text-xl font-bold">
          Main drawer 
        </h1>
        <h3 className="text-md font-bold mt-4 mb-2">
          Details: 
        </h3>
        <div className='flex flex-col gap-2 m-2'>
          <div className='text-sm'>
            Dessert: {data?.name}
          </div> 
          <TypedController
              name="calories"
              render={({ value: number, onChange }) => (
                <div className="flex flex-row items-center">
                  <TextField id="outlined-basic" label="Outlined" variant="outlined" value={value} onChange={onChange}/>
                </div>
              )}
            />
          {/* <div className='text-sm'>
            Calories: {data?.calories}
          </div> */}
          <div className='text-sm'>
            Fat (g): {data?.fat}
          </div>
          <div className='text-sm'>
            Carbs (g): {data?.carbs}
          </div>
          <div className='text-sm'>
            Protein (g): {data?.protein}
          </div>
        </div>
        <Button type="submit">Save</Button>
        <Divider />
      </Box>
    </form>
  );

  return (
    <div>
      <Drawer open={data ? true : false} anchor={"right"} onClose={onClose}>
        {DrawerList}
      </Drawer>
    </div>
  );
}

export default DrawerMainTable
