import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { useState } from 'react';
import DrawerBtcTable from './DrawerBtcTable';
import { PriceType } from './MainTable';

type TableBTCProps = {
  data: {
    bpi: {
      USD: PriceType,
      GBP: PriceType,
      EUR: PriceType
    }
  }
}

const TableBTC = (props: TableBTCProps) => {
  const { data } = props
  const [dataBtcDrawer, setDataBtcDrawer] = useState<PriceType | null>(null)

  const [btcPrice, setBtcPrice] = useState<PriceType[]>([data.bpi.USD, data.bpi.GBP, data.bpi.EUR])

  return (
    <>
      <div className='rounded-lg overflow-hidden shadow-light-300 shadow-lg'>
        <TableContainer component={Paper} >
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead style={{backgroundColor: "#EFF6F6"}}>
              <TableRow>
                <TableCell>Code</TableCell>
                <TableCell align="left">Symbol</TableCell>
                <TableCell align="left">Rate</TableCell>
                <TableCell align="left">Description</TableCell>
                <TableCell align="left">Rate Float</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {btcPrice.map((row) => (
                <TableRow 
                  onClick={() => {setDataBtcDrawer(row)}}
                  key={row.code}
                  className='hover:cursor-pointer hover:bg-gray-100'
                  sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    {row.code}
                  </TableCell>
                  <TableCell align="left">{row.symbol}</TableCell>
                  <TableCell align="left">{row.rate}</TableCell>
                  <TableCell align="left">{row.description}</TableCell>
                  <TableCell align="left">{row.rateFloat}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
      {
        dataBtcDrawer &&
        <DrawerBtcTable
          data={dataBtcDrawer}
          onClose={() => {setDataBtcDrawer(null)}}
          saveForm={setBtcPrice}
        />
      }
    </>
  );
}

export default TableBTC
